use super::object::BodyHandle;

/// Event generated by the physics engine.  
/// In case of an overlap between a solid body and sensor the solid body is guaranteed to be the first handle.
#[derive(Debug, Clone, Copy)]
pub enum PhysicsEvent<T> {
    // TODO: Should it include the normal for 2 solid bodies or not?
    OverlapStarted(BodyHandle, BodyHandle, T, T),
    OverlapEnded(BodyHandle, BodyHandle, T, T),
    CollisionStarted(BodyHandle, BodyHandle, T, T),
    CollisionEnded(BodyHandle, BodyHandle, T, T),
}
